contracts/BusinessRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title BusinessRegistry
 * @dev Registry for business verification and data management
 */
contract BusinessRegistry is AccessControl, Pausable {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    enum BusinessStatus { Pending, Verified, Rejected, Suspended }

    struct Business {
        uint256 id;
        address owner;
        string name;
        string businessType;
        string location;
        string documentsHash; // IPFS hash
        BusinessStatus status;
        uint256 registrationDate;
        uint256 verificationDate;
        string rejectionReason;
        uint256 monthlyRevenue;
        uint256 yearsInOperation;
        uint256 employeeCount;
    }

    struct BusinessMetadata {
        string description;
        string website;
        string email;
        string phone;
        string[] categories;
        mapping(string => string) additionalData;
    }

    mapping(uint256 => Business) public businesses;
    mapping(uint256 => BusinessMetadata) private businessMetadata;
    mapping(address => uint256[]) public ownerBusinesses;
    mapping(string => bool) public businessNames; // Prevent duplicate names

    uint256 public nextBusinessId = 1;
    uint256 public totalBusinesses;
    uint256 public verifiedBusinesses;

    event BusinessRegistered(
        uint256 indexed businessId,
        address indexed owner,
        string name,
        string businessType
    );

    event BusinessVerified(
        uint256 indexed businessId,
        address indexed verifier,
        uint256 timestamp
    );

    event BusinessRejected(
        uint256 indexed businessId,
        address indexed verifier,
        string reason
    );

    event BusinessStatusUpdated(
        uint256 indexed businessId,
        BusinessStatus oldStatus,
        BusinessStatus newStatus
    );

    event BusinessDataUpdated(
        uint256 indexed businessId,
        string field,
        string newValue
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
    }

    /**
     * @dev Register a new business
     */
    function registerBusiness(
        string memory _name,
        string memory _businessType,
        string memory _location,
        string memory _documentsHash,
        uint256 _monthlyRevenue,
        uint256 _yearsInOperation,
        uint256 _employeeCount,
        string memory _description,
        string memory _website,
        string memory _email,
        string memory _phone
    ) external whenNotPaused returns (uint256) {
        require(bytes(_name).length > 0, "Business name required");
        require(bytes(_businessType).length > 0, "Business type required");
        require(bytes(_location).length > 0, "Location required");
        require(bytes(_documentsHash).length > 0, "Documents hash required");
        require(!businessNames[_name], "Business name already exists");

        uint256 businessId = nextBusinessId++;

        businesses[businessId] = Business({
            id: businessId,
            owner: msg.sender,
            name: _name,
            businessType: _businessType,
            location: _location,
            documentsHash: _documentsHash,
            status: BusinessStatus.Pending,
            registrationDate: block.timestamp,
            verificationDate: 0,
            rejectionReason: "",
            monthlyRevenue: _monthlyRevenue,
            yearsInOperation: _yearsInOperation,
            employeeCount: _employeeCount
        });

        BusinessMetadata storage metadata = businessMetadata[businessId];
        metadata.description = _description;
        metadata.website = _website;
        metadata.email = _email;
        metadata.phone = _phone;

        ownerBusinesses[msg.sender].push(businessId);
        businessNames[_name] = true;
        totalBusinesses++;

        emit BusinessRegistered(businessId, msg.sender, _name, _businessType);
        return businessId;
    }

    /**
     * @dev Verify a business
     */
    function verifyBusiness(uint256 _businessId) 
        external 
        onlyRole(VERIFIER_ROLE) 
        whenNotPaused 
    {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");
        require(business.status == BusinessStatus.Pending, "Business not pending verification");

        BusinessStatus oldStatus = business.status;
        business.status = BusinessStatus.Verified;
        business.verificationDate = block.timestamp;
        verifiedBusinesses++;

        emit BusinessVerified(_businessId, msg.sender, block.timestamp);
        emit BusinessStatusUpdated(_businessId, oldStatus, BusinessStatus.Verified);
    }

    /**
     * @dev Reject a business
     */
    function rejectBusiness(uint256 _businessId, string memory _reason) 
        external 
        onlyRole(VERIFIER_ROLE) 
        whenNotPaused 
    {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");
        require(business.status == BusinessStatus.Pending, "Business not pending verification");
        require(bytes(_reason).length > 0, "Rejection reason required");

        BusinessStatus oldStatus = business.status;
        business.status = BusinessStatus.Rejected;
        business.rejectionReason = _reason;

        emit BusinessRejected(_businessId, msg.sender, _reason);
        emit BusinessStatusUpdated(_businessId, oldStatus, BusinessStatus.Rejected);
    }

    /**
     * @dev Update business status (admin only)
     */
    function updateBusinessStatus(uint256 _businessId, BusinessStatus _newStatus) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");

        BusinessStatus oldStatus = business.status;

        // Update verified count
        if (oldStatus == BusinessStatus.Verified && _newStatus != BusinessStatus.Verified) {
            verifiedBusinesses--;
        } else if (oldStatus != BusinessStatus.Verified && _newStatus == BusinessStatus.Verified) {
            verifiedBusinesses++;
        }

        business.status = _newStatus;

        emit BusinessStatusUpdated(_businessId, oldStatus, _newStatus);
    }

    /**
     * @dev Update business documents
     */
    function updateBusinessDocuments(uint256 _businessId, string memory _newDocumentsHash) 
        external 
    {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");
        require(msg.sender == business.owner, "Only business owner can update");
        require(bytes(_newDocumentsHash).length > 0, "Documents hash required");

        business.documentsHash = _newDocumentsHash;
        // Reset status to pending if it was rejected
        if (business.status == BusinessStatus.Rejected) {
            business.status = BusinessStatus.Pending;
            business.rejectionReason = "";
        }

        emit BusinessDataUpdated(_businessId, "documentsHash", _newDocumentsHash);
    }

    /**
     * @dev Update business metadata
     */
    function updateBusinessMetadata(
        uint256 _businessId,
        string memory _description,
        string memory _website,
        string memory _email,
        string memory _phone
    ) external {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");
        require(msg.sender == business.owner, "Only business owner can update");

        BusinessMetadata storage metadata = businessMetadata[_businessId];
        metadata.description = _description;
        metadata.website = _website;
        metadata.email = _email;
        metadata.phone = _phone;

        emit BusinessDataUpdated(_businessId, "metadata", "updated");
    }

    /**
     * @dev Add business category
     */
    function addBusinessCategory(uint256 _businessId, string memory _category) 
        external 
    {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");
        require(msg.sender == business.owner, "Only business owner can update");

        businessMetadata[_businessId].categories.push(_category);
        emit BusinessDataUpdated(_businessId, "category", _category);
    }

    /**
     * @dev Set additional business data
     */
    function setAdditionalData(
        uint256 _businessId, 
        string memory _key, 
        string memory _value
    ) external {
        Business storage business = businesses[_businessId];
        require(business.id != 0, "Business does not exist");
        require(msg.sender == business.owner, "Only business owner can update");

        businessMetadata[_businessId].additionalData[_key] = _value;
        emit BusinessDataUpdated(_businessId, _key, _value);
    }

    /**
     * @dev Get business details
     */
    function getBusiness(uint256 _businessId) 
        external 
        view 
        returns (Business memory) 
    {
        require(businesses[_businessId].id != 0, "Business does not exist");
        return businesses[_businessId];
    }

    /**
     * @dev Get business metadata
     */
    function getBusinessMetadata(uint256 _businessId) 
        external 
        view 
        returns (
            string memory description,
            string memory website,
            string memory email,
            string memory phone,
            string[] memory categories
        ) 
    {
        require(businesses[_businessId].id != 0, "Business does not exist");
        BusinessMetadata storage metadata = businessMetadata[_businessId];
        return (
            metadata.description,
            metadata.website,
            metadata.email,
            metadata.phone,
            metadata.categories
        );
    }

    /**
     * @dev Get additional business data
     */
    function getAdditionalData(uint256 _businessId, string memory _key) 
        external 
        view 
        returns (string memory) 
    {
        require(businesses[_businessId].id != 0, "Business does not exist");
        return businessMetadata[_businessId].additionalData[_key];
    }

    /**
     * @dev Get businesses owned by address
     */
    function getOwnerBusinesses(address _owner) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return ownerBusinesses[_owner];
    }

    /**
     * @dev Check if business is verified
     */
    function isBusinessVerified(uint256 _businessId) 
        external 
        view 
        returns (bool) 
    {
        return businesses[_businessId].status == BusinessStatus.Verified;
    }

    /**
     * @dev Get business statistics
     */
    function getBusinessStats() 
        external 
        view 
        returns (
            uint256 total,
            uint256 verified,
            uint256 pending,
            uint256 rejected
        ) 
    {
        uint256 pendingCount = 0;
        uint256 rejectedCount = 0;

        for (uint256 i = 1; i < nextBusinessId; i++) {
            if (businesses[i].status == BusinessStatus.Pending) {
                pendingCount++;
            } else if (businesses[i].status == BusinessStatus.Rejected) {
                rejectedCount++;
            }
        }

        return (totalBusinesses, verifiedBusinesses, pendingCount, rejectedCount);
    }

    /**
     * @dev Pause contract (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

contracts/EscrowManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title EscrowManager
 * @dev Manages escrow for investment funds with milestone-based releases
 */
contract EscrowManager is ReentrancyGuard, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant BUSINESS_ROLE = keccak256("BUSINESS_ROLE");
    bytes32 public constant ARBITRATOR_ROLE = keccak256("ARBITRATOR_ROLE");

    IERC20 public immutable cUSD;

    enum EscrowStatus { Active, Released, Disputed, Cancelled }
    enum DisputeStatus { None, Raised, UnderReview, Resolved }

    struct Escrow {
        uint256 id;
        uint256 poolId;
        address business;
        address[] investors;
        uint256 totalAmount;
        uint256 releasedAmount;
        EscrowStatus status;
        uint256 createdAt;
        uint256 releaseDeadline;
        string milestoneDescription;
        string evidenceHash; // IPFS hash of milestone evidence
    }

    struct Dispute {
        uint256 escrowId;
        address initiator;
        string reason;
        DisputeStatus status;
        uint256 createdAt;
        uint256 resolvedAt;
        address resolver;
        bool businessFavor; // true if resolved in business favor
        string resolution;
    }

    struct InvestorVote {
        address investor;
        bool approve;
        uint256 timestamp;
        string comment;
    }

    mapping(uint256 => Escrow) public escrows;
    mapping(uint256 => Dispute) public disputes;
    mapping(uint256 => InvestorVote[]) public escrowVotes;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(address => uint256[]) public businessEscrows;
    mapping(address => uint256[]) public investorEscrows;

    uint256 public nextEscrowId = 1;
    uint256 public votingPeriod = 7 days;
    uint256 public requiredApprovalPercentage = 60; // 60% approval needed

    event EscrowCreated(
        uint256 indexed escrowId,
        uint256 indexed poolId,
        address indexed business,
        uint256 amount
    );

    event MilestoneSubmitted(
        uint256 indexed escrowId,
        string evidenceHash,
        string description
    );

    event VoteCast(
        uint256 indexed escrowId,
        address indexed investor,
        bool approve
    );

    event FundsReleased(
        uint256 indexed escrowId,
        uint256 amount,
        address recipient
    );

    event DisputeRaised(
        uint256 indexed escrowId,
        address indexed initiator,
        string reason
    );

    event DisputeResolved(
        uint256 indexed escrowId,
        address indexed resolver,
        bool businessFavor
    );

    constructor(address _cUSD) {
        cUSD = IERC20(_cUSD);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(ARBITRATOR_ROLE, msg.sender);
    }

    /**
     * @dev Create new escrow for milestone
     */
    function createEscrow(
        uint256 _poolId,
        address _business,
        address[] memory _investors,
        uint256 _amount,
        string memory _milestoneDescription,
        uint256 _releaseDeadline
    ) external onlyRole(ADMIN_ROLE) returns (uint256) {
        require(_amount > 0, "Amount must be positive");
        require(_investors.length > 0, "Must have investors");
        require(_releaseDeadline > block.timestamp, "Invalid deadline");

        uint256 escrowId = nextEscrowId++;

        escrows[escrowId] = Escrow({
            id: escrowId,
            poolId: _poolId,
            business: _business,
            investors: _investors,
            totalAmount: _amount,
            releasedAmount: 0,
            status: EscrowStatus.Active,
            createdAt: block.timestamp,
            releaseDeadline: _releaseDeadline,
            milestoneDescription: _milestoneDescription,
            evidenceHash: ""
        });

        businessEscrows[_business].push(escrowId);
        for (uint256 i = 0; i < _investors.length; i++) {
            investorEscrows[_investors[i]].push(escrowId);
        }

        _grantRole(BUSINESS_ROLE, _business);

        emit EscrowCreated(escrowId, _poolId, _business, _amount);
        return escrowId;
    }

    /**
     * @dev Submit milestone evidence
     */
    function submitMilestone(
        uint256 _escrowId,
        string memory _evidenceHash,
        string memory _description
    ) external {
        Escrow storage escrow = escrows[_escrowId];
        require(escrow.id != 0, "Escrow does not exist");
        require(msg.sender == escrow.business, "Only business can submit");
        require(escrow.status == EscrowStatus.Active, "Escrow not active");
        require(bytes(_evidenceHash).length > 0, "Evidence hash required");

        escrow.evidenceHash = _evidenceHash;
        escrow.milestoneDescription = _description;

        emit MilestoneSubmitted(_escrowId, _evidenceHash, _description);
    }

    /**
     * @dev Cast vote on milestone
     */
    function castVote(
        uint256 _escrowId,
        bool _approve,
        string memory _comment
    ) external nonReentrant {
        Escrow storage escrow = escrows[_escrowId];
        require(escrow.id != 0, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Active, "Escrow not active");
        require(bytes(escrow.evidenceHash).length > 0, "No milestone submitted");
        require(!hasVoted[_escrowId][msg.sender], "Already voted");
        require(_isInvestor(_escrowId, msg.sender), "Not an investor");

        escrowVotes[_escrowId].push(InvestorVote({
            investor: msg.sender,
            approve: _approve,
            timestamp: block.timestamp,
            comment: _comment
        }));

        hasVoted[_escrowId][msg.sender] = true;

        emit VoteCast(_escrowId, msg.sender, _approve);

        // Check if voting is complete
        _checkVotingComplete(_escrowId);
    }

    /**
     * @dev Release funds after successful voting
     */
    function releaseFunds(uint256 _escrowId) 
        external 
        nonReentrant 
        onlyRole(ADMIN_ROLE) 
    {
        Escrow storage escrow = escrows[_escrowId];
        require(escrow.id != 0, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Active, "Escrow not active");
        require(_isApproved(_escrowId), "Milestone not approved");

        uint256 releaseAmount = escrow.totalAmount - escrow.releasedAmount;
        escrow.releasedAmount = escrow.totalAmount;
        escrow.status = EscrowStatus.Released;

        cUSD.safeTransfer(escrow.business, releaseAmount);

        emit FundsReleased(_escrowId, releaseAmount, escrow.business);
    }

    /**
     * @dev Raise dispute
     */
    function raiseDispute(uint256 _escrowId, string memory _reason) 
        external 
    {
        Escrow storage escrow = escrows[_escrowId];
        require(escrow.id != 0, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Active, "Escrow not active");
        require(
            _isInvestor(_escrowId, msg.sender) || msg.sender == escrow.business,
            "Not authorized"
        );
        require(disputes[_escrowId].status == DisputeStatus.None, "Dispute already exists");

        disputes[_escrowId] = Dispute({
            escrowId: _escrowId,
            initiator: msg.sender,
            reason: _reason,
            status: DisputeStatus.Raised,
            createdAt: block.timestamp,
            resolvedAt: 0,
            resolver: address(0),
            businessFavor: false,
            resolution: ""
        });

        escrow.status = EscrowStatus.Disputed;

        emit DisputeRaised(_escrowId, msg.sender, _reason);
    }

    /**
     * @dev Resolve dispute (arbitrator only)
     */
    function resolveDispute(
        uint256 _escrowId,
        bool _businessFavor,
        string memory _resolution
    ) external onlyRole(ARBITRATOR_ROLE) {
        Dispute storage dispute = disputes[_escrowId];
        Escrow storage escrow = escrows[_escrowId];

        require(dispute.status == DisputeStatus.Raised, "No active dispute");
        require(escrow.status == EscrowStatus.Disputed, "Escrow not disputed");

        dispute.status = DisputeStatus.Resolved;
        dispute.resolvedAt = block.timestamp;
        dispute.resolver = msg.sender;
        dispute.businessFavor = _businessFavor;
        dispute.resolution = _resolution;

        if (_businessFavor) {
            // Release funds to business
            uint256 releaseAmount = escrow.totalAmount - escrow.releasedAmount;
            escrow.releasedAmount = escrow.totalAmount;
            escrow.status = EscrowStatus.Released;
            cUSD.safeTransfer(escrow.business, releaseAmount);
        } else {
            // Return funds to investors proportionally
            escrow.status = EscrowStatus.Cancelled;
            _returnFundsToInvestors(_escrowId);
        }

        emit DisputeResolved(_escrowId, msg.sender, _businessFavor);
    }

    /**
     * @dev Emergency cancel escrow (admin only)
     */
    function emergencyCancel(uint256 _escrowId) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        Escrow storage escrow = escrows[_escrowId];
        require(escrow.id != 0, "Escrow does not exist");
        require(escrow.status == EscrowStatus.Active, "Escrow not active");

        escrow.status = EscrowStatus.Cancelled;
        _returnFundsToInvestors(_escrowId);
    }

    /**
     * @dev Check if milestone is approved by investors
     */
    function _isApproved(uint256 _escrowId) internal view returns (bool) {
        InvestorVote[] memory votes = escrowVotes[_escrowId];
        Escrow memory escrow = escrows[_escrowId];

        if (votes.length < escrow.investors.length) {
            return false; // Not all investors voted
        }

        uint256 approvals = 0;
        for (uint256 i = 0; i < votes.length; i++) {
            if (votes[i].approve) {
                approvals++;
            }
        }

        return (approvals * 100) / votes.length >= requiredApprovalPercentage;
    }

    /**
     * @dev Check if address is investor in escrow
     */
    function _isInvestor(uint256 _escrowId, address _address) 
        internal 
        view 
        returns (bool) 
    {
        Escrow memory escrow = escrows[_escrowId];
        for (uint256 i = 0; i < escrow.investors.length; i++) {
            if (escrow.investors[i] == _address) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Check if voting is complete and process result
     */
    function _checkVotingComplete(uint256 _escrowId) internal {
        Escrow memory escrow = escrows[_escrowId];
        InvestorVote[] memory votes = escrowVotes[_escrowId];

        if (votes.length == escrow.investors.length) {
            // All investors have voted
            if (_isApproved(_escrowId)) {
                // Auto-release if approved
                // Note: In production, you might want manual release for additional checks
            }
        }
    }

    /**
     * @dev Return funds to investors proportionally
     */
    function _returnFundsToInvestors(uint256 _escrowId) internal {
        Escrow memory escrow = escrows[_escrowId];
        uint256 returnAmount = escrow.totalAmount - escrow.releasedAmount;

        // In a real implementation, you would need investment amounts per investor
        // For now, distribute equally
        uint256 amountPerInvestor = returnAmount / escrow.investors.length;

        for (uint256 i = 0; i < escrow.investors.length; i++) {
            cUSD.safeTransfer(escrow.investors[i], amountPerInvestor);
        }
    }

    /**
     * @dev Get escrow details
     */
    function getEscrow(uint256 _escrowId) 
        external 
        view 
        returns (Escrow memory) 
    {
        return escrows[_escrowId];
    }

    /**
     * @dev Get escrow votes
     */
    function getEscrowVotes(uint256 _escrowId) 
        external 
        view 
        returns (InvestorVote[] memory) 
    {
        return escrowVotes[_escrowId];
    }

    /**
     * @dev Get business escrows
     */
    function getBusinessEscrows(address _business) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return businessEscrows[_business];
    }

    /**
     * @dev Get investor escrows
     */
    function getInvestorEscrows(address _investor) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return investorEscrows[_investor];
    }

    /**
     * @dev Set voting parameters (admin only)
     */
    function setVotingParameters(
        uint256 _votingPeriod,
        uint256 _requiredApprovalPercentage
    ) external onlyRole(ADMIN_ROLE) {
        require(_requiredApprovalPercentage > 0 && _requiredApprovalPercentage <= 100, "Invalid percentage");
        votingPeriod = _votingPeriod;
        requiredApprovalPercentage = _requiredApprovalPercentage;
    }
}

contracts/InvestmentPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title InvestmentPool
 * @dev Manages investment pools for local businesses with milestone-based fund release
 */
contract InvestmentPool is ReentrancyGuard, AccessControl, Pausable {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant BUSINESS_ROLE = keccak256("BUSINESS_ROLE");

    IERC20 public immutable cUSD;
    uint256 public constant PLATFORM_FEE = 200; // 2% in basis points
    uint256 public constant BASIS_POINTS = 10000;

    enum PoolStatus { Active, Funded, Closed, Defaulted }
    enum MilestoneStatus { Pending, Approved, Rejected }

    struct Pool {
        uint256 id;
        address businessOwner;
        uint256 targetAmount;
        uint256 currentAmount;
        uint256 duration; // in months
        uint256 interestRate; // annual rate in basis points
        PoolStatus status;
        uint256 createdAt;
        uint256 fundingDeadline;
        string businessId;
        string purpose;
    }

    struct Investment {
        address investor;
        uint256 amount;
        uint256 timestamp;
        bool withdrawn;
    }

    struct Milestone {
        uint256 poolId;
        string description;
        uint256 amount;
        MilestoneStatus status;
        uint256 dueDate;
        string evidence;
    }

    struct RepaymentSchedule {
        uint256 poolId;
        uint256 amount;
        uint256 dueDate;
        bool paid;
    }

    mapping(uint256 => Pool) public pools;
    mapping(uint256 => Investment[]) public poolInvestments;
    mapping(uint256 => Milestone[]) public poolMilestones;
    mapping(uint256 => RepaymentSchedule[]) public repaymentSchedules;
    mapping(address => uint256[]) public investorPools;
    mapping(address => uint256[]) public businessPools;

    uint256 public nextPoolId = 1;
    address public feeRecipient;

    event PoolCreated(
        uint256 indexed poolId,
        address indexed businessOwner,
        uint256 targetAmount,
        uint256 duration,
        string businessId
    );

    event InvestmentMade(
        uint256 indexed poolId,
        address indexed investor,
        uint256 amount
    );

    event FundsReleased(
        uint256 indexed poolId,
        uint256 amount,
        uint256 milestoneIndex
    );

    event RepaymentMade(
        uint256 indexed poolId,
        uint256 amount,
        uint256 scheduleIndex
    );

    event PoolClosed(uint256 indexed poolId, PoolStatus status);

    constructor(address _cUSD, address _feeRecipient) {
        cUSD = IERC20(_cUSD);
        feeRecipient = _feeRecipient;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new investment pool
     */
    function createPool(
        uint256 _targetAmount,
        uint256 _duration,
        uint256 _interestRate,
        string memory _businessId,
        string memory _purpose
    ) external returns (uint256) {
        require(_targetAmount > 0, "Target amount must be positive");
        require(_duration > 0 && _duration <= 60, "Duration must be 1-60 months");
        require(_interestRate >= 500 && _interestRate <= 1500, "Interest rate must be 5-15%");

        uint256 poolId = nextPoolId++;

        pools[poolId] = Pool({
            id: poolId,
            businessOwner: msg.sender,
            targetAmount: _targetAmount,
            currentAmount: 0,
            duration: _duration,
            interestRate: _interestRate,
            status: PoolStatus.Active,
            createdAt: block.timestamp,
            fundingDeadline: block.timestamp + 30 days,
            businessId: _businessId,
            purpose: _purpose
        });

        businessPools[msg.sender].push(poolId);
        _grantRole(BUSINESS_ROLE, msg.sender);

        emit PoolCreated(poolId, msg.sender, _targetAmount, _duration, _businessId);
        return poolId;
    }

    /**
     * @dev Invest in a pool
     */
    function invest(uint256 _poolId, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
    {
        Pool storage pool = pools[_poolId];
        require(pool.status == PoolStatus.Active, "Pool not active");
        require(block.timestamp <= pool.fundingDeadline, "Funding deadline passed");
        require(_amount > 0, "Investment amount must be positive");
        require(pool.currentAmount + _amount <= pool.targetAmount, "Exceeds target amount");
        require(_amount >= 1e18, "Minimum investment is 1 cUSD"); // $1 minimum
        require(_amount <= 50e18, "Maximum investment is 50 cUSD"); // $50 maximum

        cUSD.safeTransferFrom(msg.sender, address(this), _amount);

        poolInvestments[_poolId].push(Investment({
            investor: msg.sender,
            amount: _amount,
            timestamp: block.timestamp,
            withdrawn: false
        }));

        pool.currentAmount += _amount;
        investorPools[msg.sender].push(_poolId);

        // Check if pool is fully funded
        if (pool.currentAmount >= pool.targetAmount) {
            pool.status = PoolStatus.Funded;
            _createRepaymentSchedule(_poolId);
        }

        emit InvestmentMade(_poolId, msg.sender, _amount);
    }

    /**
     * @dev Release funds to business owner after milestone approval
     */
    function releaseFunds(uint256 _poolId, uint256 _milestoneIndex) 
        external 
        onlyRole(ADMIN_ROLE) 
        nonReentrant 
    {
        Pool storage pool = pools[_poolId];
        require(pool.status == PoolStatus.Funded, "Pool not funded");

        Milestone storage milestone = poolMilestones[_poolId][_milestoneIndex];
        require(milestone.status == MilestoneStatus.Approved, "Milestone not approved");

        uint256 platformFee = (milestone.amount * PLATFORM_FEE) / BASIS_POINTS;
        uint256 businessAmount = milestone.amount - platformFee;

        cUSD.safeTransfer(pool.businessOwner, businessAmount);
        cUSD.safeTransfer(feeRecipient, platformFee);

        emit FundsReleased(_poolId, milestone.amount, _milestoneIndex);
    }

    /**
     * @dev Process repayment from business
     */
    function processRepayment(uint256 _poolId, uint256 _scheduleIndex) 
        external 
        nonReentrant 
    {
        Pool storage pool = pools[_poolId];
        require(msg.sender == pool.businessOwner, "Only business owner can repay");

        RepaymentSchedule storage schedule = repaymentSchedules[_poolId][_scheduleIndex];
        require(!schedule.paid, "Already paid");
        require(block.timestamp <= schedule.dueDate + 7 days, "Payment window expired");

        cUSD.safeTransferFrom(msg.sender, address(this), schedule.amount);
        schedule.paid = true;

        // Distribute to investors proportionally
        _distributeRepayment(_poolId, schedule.amount);

        emit RepaymentMade(_poolId, schedule.amount, _scheduleIndex);
    }

    /**
     * @dev Emergency pause functionality
     */
    function emergencyPause(uint256 _poolId) external onlyRole(ADMIN_ROLE) {
        pools[_poolId].status = PoolStatus.Closed;
        emit PoolClosed(_poolId, PoolStatus.Closed);
    }

    /**
     * @dev Handle default situation
     */
    function handleDefault(uint256 _poolId) external onlyRole(ADMIN_ROLE) {
        pools[_poolId].status = PoolStatus.Defaulted;
        emit PoolClosed(_poolId, PoolStatus.Defaulted);
    }

    /**
     * @dev Add milestone to pool
     */
    function addMilestone(
        uint256 _poolId,
        string memory _description,
        uint256 _amount,
        uint256 _dueDate
    ) external {
        Pool storage pool = pools[_poolId];
        require(msg.sender == pool.businessOwner, "Only business owner");
        require(pool.status == PoolStatus.Funded, "Pool not funded");

        poolMilestones[_poolId].push(Milestone({
            poolId: _poolId,
            description: _description,
            amount: _amount,
            status: MilestoneStatus.Pending,
            dueDate: _dueDate,
            evidence: ""
        }));
    }

    /**
     * @dev Approve milestone
     */
    function approveMilestone(uint256 _poolId, uint256 _milestoneIndex) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        poolMilestones[_poolId][_milestoneIndex].status = MilestoneStatus.Approved;
    }

    /**
     * @dev Create repayment schedule for funded pool
     */
    function _createRepaymentSchedule(uint256 _poolId) internal {
        Pool storage pool = pools[_poolId];
        uint256 totalRepayment = pool.targetAmount + 
            (pool.targetAmount * pool.interestRate * pool.duration) / (BASIS_POINTS * 12);
        uint256 monthlyPayment = totalRepayment / pool.duration;

        for (uint256 i = 0; i < pool.duration; i++) {
            repaymentSchedules[_poolId].push(RepaymentSchedule({
                poolId: _poolId,
                amount: monthlyPayment,
                dueDate: block.timestamp + ((i + 1) * 30 days),
                paid: false
            }));
        }
    }

    /**
     * @dev Distribute repayment to investors proportionally
     */
    function _distributeRepayment(uint256 _poolId, uint256 _amount) internal {
        Pool storage pool = pools[_poolId];
        Investment[] storage investments = poolInvestments[_poolId];

        for (uint256 i = 0; i < investments.length; i++) {
            if (!investments[i].withdrawn) {
                uint256 investorShare = (_amount * investments[i].amount) / pool.targetAmount;
                cUSD.safeTransfer(investments[i].investor, investorShare);
            }
        }
    }

    /**
     * @dev Get pool details
     */
    function getPool(uint256 _poolId) external view returns (Pool memory) {
        return pools[_poolId];
    }

    /**
     * @dev Get pool investments
     */
    function getPoolInvestments(uint256 _poolId) external view returns (Investment[] memory) {
        return poolInvestments[_poolId];
    }

    /**
     * @dev Get investor pools
     */
    function getInvestorPools(address _investor) external view returns (uint256[] memory) {
        return investorPools[_investor];
    }

    /**
     * @dev Get business pools
     */
    function getBusinessPools(address _business) external view returns (uint256[] memory) {
        return businessPools[_business];
    }
}

contracts/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract MockERC20 {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor(string memory _name, string memory _symbol, uint8 _decimals) {
        name = _name;
        symbol = _symbol;
        decimals = _decimals;
    }

    function mint(address to, uint256 amount) external {
        balanceOf[to] += amount;
        totalSupply += amount;
        emit Transfer(address(0), to, amount);
    }

    function transfer(address to, uint256 amount) external returns (bool) {
        require(balanceOf[msg.sender] >= amount, "Insufficient balance");
        balanceOf[msg.sender] -= amount;
        balanceOf[to] += amount;
        emit Transfer(msg.sender, to, amount);
        return true;
    }

    function approve(address spender, uint256 amount) external returns (bool) {
        allowance[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address from, address to, uint256 amount) external returns (bool) {
        require(balanceOf[from] >= amount, "Insufficient balance");
        require(allowance[from][msg.sender] >= amount, "Insufficient allowance");

        balanceOf[from] -= amount;
        balanceOf[to] += amount;
        allowance[from][msg.sender] -= amount;

        emit Transfer(from, to, amount);
        return true;
    }
}

